mapper.select:
select
    pt.id,
    um.name,
    pt.period
from
    period_tbl pt
inner join
    user_mst um
on
    pt.id = um.id


      pt.id = #{id}


      and pt.period >= #{periodFrom}
      and pt.period <= #{periodTo}


      and pt.period >= #{periodFrom}


      and pt.period <= #{periodTo}

mapper.delete:
delete from
    period_tbl
where
    id     = #{id}
and period = #{period}

mapper.insert:
insert into period_tbl (
    id,
    period
)
values (
    #{id},
    #{period}
)
